function अंतर्लिखितवर्गखोजें(वक्र) {
  let सर्वोत्तमवर्ग = [];
  let अधिकतमआकार = 0;

  for (let i = 0; i < 1000; i++) {
    const केंद्र = यादृच्छिकअंदरकाबिंदुप्राप्तकरें(वक्र);
    const वर्ग = बिंदुपरसबसेबड़ावर्गखोजें(केंद्र, वक्र);
    if (वर्ग.आकार > अधिकतमआकार) {
      अधिकतमआकार = वर्ग.आकार;
      सर्वोत्तमवर्ग = वर्ग.बिंदु;
    }
  }

  return सर्वोत्तमवर्ग;
}

