function विस्तारितवर्गखोजें(वक्र) {
  const न्यूनतमX = Math.min(...वक्र.map(p => p.x));
  const अधिकतमX = Math.max(...वक्र.map(p => p.x));
  const न्यूनतमY = Math.min(...वक्र.map(p => p.y));
  const अधिकतमY = Math.max(...वक्र.map(p => p.y));

  const केंद्रX = (न्यूनतमX + अधिकतमX) / 2;
  const केंद्रY = (न्यूनतमY + अधिकतमY) / 2;
  const आकार = Math.max(अधिकतमX - न्यूनतमX, अधिकतमY - न्यूनतमY);

  return [
    { x: केंद्रX - आकार / 2, y: केंद्रY - आकार / 2 },
    { x: केंद्रX + आकार / 2, y: केंद्रY - आकार / 2 },
    { x: केंद्रX + आकार / 2, y: केंद्रY + आकार / 2 },
    { x: केंद्रX - आकार / 2, y: केंद्रY + आकार / 2 },
  ];
}
