function найтиВписанныйКвадрат(кривая) {
  let лучшийКвадрат = [];
  let максРазмер = 0;

  for (let i = 0; i < 1000; i++) {
    const центр = получитьСлучайнуюТочкуВнутри(кривая);
    const квадрат = найтиНаибольшийКвадратВТочке(центр, кривая);
    if (квадрат.размер > максРазмер) {
      максРазмер = квадрат.размер;
      лучшийКвадрат = квадрат.точки;
    }
  }

  return лучшийКвадрат;
}

algorithm.extendedCode:
function найтиОписанныйКвадрат(кривая) {
  const минX = Math.min(...кривая.map(p => p.x));
  const максX = Math.max(...кривая.map(p => p.x));
  const минY = Math.min(...кривая.map(p => p.y));
  const максY = Math.max(...кривая.map(p => p.y));

  const центрX = (минX + максX) / 2;
  const центрY = (минY + максY) / 2;
  const размер = Math.max(максX - минX, максY - минY);

  return [
    { x: центрX - размер / 2, y: центрY - размер / 2 },
    { x: центрX + размер / 2, y: центрY - размер / 2 },
    { x: центрX + размер / 2, y: центрY + размер / 2 },
    { x: центрX - размер / 2, y: центрY + размер / 2 },
  ];
}
