function знайтиВписанийКвадрат(крива) {
  let найкращийКвадрат = [];
  let максРозмір = 0;

  for (let i = 0; i < 1000; i++) {
    const центр = отриматиВипадковуТочкуВсередині(крива);
    const квадрат = знайтиНайбільшийКвадратВТочці(центр, крива);
    if (квадрат.розмір > максРозмір) {
      максРозмір = квадрат.розмір;
      найкращийКвадрат = квадрат.точки;
    }
  }

  return найкращийКвадрат;
}

