function знайтиОписанийКвадрат(крива) {
  const мінX = Math.min(...крива.map(p => p.x));
  const максX = Math.max(...крива.map(p => p.x));
  const мінY = Math.min(...крива.map(p => p.y));
  const максY = Math.max(...крива.map(p => p.y));

  const центрX = (мінX + максX) / 2;
  const центрY = (мінY + максY) / 2;
  const розмір = Math.max(максX - мінX, максY - мінY);

  return [
    { x: центрX - розмір / 2, y: центрY - розмір / 2 },
    { x: центрX + розмір / 2, y: центрY - розмір / 2 },
    { x: центрX + розмір / 2, y: центрY + розмір / 2 },
    { x: центрX - розмір / 2, y: центрY + розмір / 2 },
  ];
}
